import React, { useEffect, useState } from 'react'

import { useDispatch, useSelector } from 'react-redux'
import { login } from '../../actions/authActions'

const LoginScreen = ({ location, history }) => {
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')

  const dispatch = useDispatch()

  const userLogin = useSelector((state) => state.userLogin)
  const { loading, error, userInfo } = userLogin

  const [show, setShow] = useState(false)

  // const redirect = location.search ? location.search.split('=')[1]: '/'

  useEffect(() => {
    if (userInfo) {
      history.push('/')
    }
  }, [history, userInfo])

  const loginButton = (e) => {
    e.preventDefault()
    // dispatch(login(username, password))
    setShow(false)
    // const dat = document.getElementById('root')
    // let modalback = document.getElementsByClassName('modal-backdrop')
    // console.log('dat = = ', document.getElementsByClassName('modal-backdrop'))
    // var myModalEl = document.querySelectorAll('.modal');
    // console.log('mymodal = ', myModalEl)
  }

  return (
    <>
      <section class="wrapper bg-light">
        <div class="container py-14 py-md-16 text-center">
          <div class="row">
            <div class="col-md-9 col-lg-7 col-xl-7 mx-auto text-center">
              <form className="text-start mb-3" onSubmit={loginButton}>
                <div className="form-floating mb-4">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                  />
                  <label htmlFor="loginEmail">Username</label>
                </div>
                <div className="form-floating mb-4">
                  <input
                    type="password"
                    className="form-control"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                  <label htmlFor="loginPassword">Password</label>
                </div>
                <button className="btn btn-primary rounded-pill btn-login w-100 mb-2">
                  Log In
                </button>
              </form>
            </div>
          </div>
        </div>
      </section>
    </>
  )
}

export default LoginScreen
