import axios from "axios"
import { ADD_USER_FAIL, ADD_USER_REQUEST, ADD_USER_SUCCESS } from "../constants/authConstants"

export const addUser = (payload) => async (dispatch) => {
    try {
        dispatch({
            type: ADD_USER_REQUEST
        })

        const config = {
            headers: {
                'Content-Type': 'application/json'
            }
        }

        const { data } = await axios.post(`/api/signup`,{payload}, config)

        console.log('data profile = ', data)

        dispatch({
            type: ADD_USER_SUCCESS,
            payload: data
        })

    } catch (error) {
        dispatch({
            type: ADD_USER_FAIL,
            payload:
              error.response && error.response.data.message
                ? error.response.data.message
                : error.message,
          })
    }
}
